const QuestionInterface = ({gameStarted, timeLeft, checkAnswer, onNextClick, questionArray, currentIndex, passed}) => {
    

    //determine the color based on the option clicked

    let bColor = 'linear-gradient(285deg, rgba(214, 213, 213, 0.38)50%, rgba(98, 124, 177, 0.58)50%)';
    if(timeLeft > 0 && passed=='true'){
        bColor = 'green';
    }
    if(timeLeft > 0 && passed=='false'){
        bColor = 'red';
    }
    if(timeLeft <=0 ){
        bColor = 'yellow';
    }


    return (
        

            <div style={{opacity: `${timeLeft > 0 ? '1' : '0.7' }`}} className={`question-container ${gameStarted && (timeLeft >= 0) ? 'show-question' : ''}`}>
            <div className='question-display'>
                {`Q${currentIndex+1}: ${questionArray[currentIndex].question}`}
            </div>
            <div className='answers'>
                {questionArray[currentIndex].options.map((option, id)=>(
                    
                    <p key={id} onClick={()=>checkAnswer(option)} style={{background: `${questionArray[currentIndex].answer}`, pointerEvents: `${timeLeft <= 0 ? 'none' : 'all'}`}}>

                        {option}

                    </p>
                ))}

                <div className={`next-button ${timeLeft <= 0 ? 'show-next': '' }`}onClick={onNextClick}><span> Next &gt;&gt;</span ></div>
            </div>

            
        </div>
    )
}

export default QuestionInterface
