import TopBar from "./components/TopBar"
import MenuOptions from "./components/MenuOptions";
import PlayButton from "./components/PlayButton";
import Sound from 'react-sound';
import myD from './sounds/4 final mid game.mp3';

import { useState } from "react";

const App = () => {
   
    const [onSound, setOnSound] = useState(true);
    const [gameCat, setGameCat] = useState('books');
    const [gameLevel, setGameLevel] = useState('eazy');
    const [displayMenu, setDisplayMenu] = useState(false);
    const [isPlaying, setIsPlaying] = useState(false);


   


    // Menu Bar Actions

    const handleMenuClick = ()=>{
        setDisplayMenu(!displayMenu);
    }


    // Update Category Chosen by the user

    const updateCategory = (chosenCat)=>{
        setGameCat(chosenCat)
    }

    //Update Game Level set by the user

    const updateLevel = (level)=>{
        setGameLevel(level)
    }


    //Play Actions

    const play = ()=>{
        if(onSound){
            setIsPlaying(!isPlaying)
        }
        
    }
    
    
    //Handle Sound ON/OFF
    const toggleOnSound = (resp)=>{
        setOnSound(resp)

        if(onSound){
            setIsPlaying(false)

        }else if(!onSound && !isPlaying) {
            setIsPlaying(!isPlaying)
        }
    }
    



    //Rendering Zone

    return (
        <>  
            <TopBar onclick={handleMenuClick} displayMenu={displayMenu}/>
            <MenuOptions  displayMenu={displayMenu}  toggleOnSound={toggleOnSound}  onSound={onSound} play={play} updateCategory={updateCategory} updateLevel={updateLevel}/>
            <PlayButton play={play} displayMenu={displayMenu}/>
            <Sound playStatus={isPlaying && onSound ? Sound.status.PLAYING : Sound.status.STOPPED}  url={myD} loop={true}/>
           
        </>
    )
}

export default App
