import TopBar from "./components/TopBar"
import MenuOptions from "./components/MenuOptions";
import PlayButton from "./components/PlayButton";

import { useState } from "react";

const App = () => {
    const menu = document.querySelector('.menu');
    const [displayMenu, setDisplayMenu] = useState(false);

   
    // Menu Bar Actions

    const handleMenuClick = (element)=>{
        setDisplayMenu(!displayMenu);
    }


    //Play Actions


    const play = ()=>{
        let audio = document.createElement('audio');
        audio.src = '.'
        console.log('playing my jam')
    }

    return (
        <>  
            <TopBar onclick={handleMenuClick} menuItems={menu} displayMenu={displayMenu}/>
            <MenuOptions displayMenu={displayMenu}/>
            <PlayButton play={play} displayMenu={displayMenu}/>
        </>
    )
}

export default App
