import TopBar from "./components/TopBar"
import MenuOptions from "./components/MenuOptions";
import PlayButton from "./components/PlayButton";
import GamePlayInfo from "./components/GamePlayInfo";
import EndButton from "./components/EndButton";
import QuestionInterface from "./components/QuestionInterface";
import Footer from "./components/Footer";
import Sound from 'react-sound';
import myD from './sounds/4 final mid game.mp3';

import { useState, useEffect } from "react";

const App = () => {

    const settingsFromBackUp = window.localStorage.getItem('settings');
    const settingData = JSON.parse(settingsFromBackUp);
   
    const [onSound, setOnSound] = useState(settingsFromBackUp ? settingData.onSound : true);
    const [gameCat, setGameCat] = useState(settingsFromBackUp ? settingData.gameCat : 'books');
    const [gameLevel, setGameLevel] = useState(settingsFromBackUp ? settingData.gameLevel : 'eazy');
    const [displayMenu, setDisplayMenu] = useState(false);
    const [isPlaying, setIsPlaying] = useState(false);
    const [repeat, setRepeat] = useState(true);
    const [gameStarted, setGameStarted] = useState(false);
    const [gameTime, setGameTime] = useState(20);
    const [timeLeft, setTimeLeft] = useState(gameTime);
    

    
    // Menu Bar Actions
    
    const handleMenuClick = ()=>{
        setDisplayMenu(!displayMenu);
    }

    
    // Update Category Chosen by the user

    const updateCategory = (chosenCat)=>{
        setGameCat(chosenCat)
    }
    
    //Update Game Level set by the user
    
    const updateLevel = (level, timerValue)=>{
        setGameLevel(level)
        // handleTimer(timerValue)
    }
    

    
    //Handle Sound ON/OFF
      const toggleOnSound = (resp)=>{
          setOnSound(resp)

        if(onSound){
            setIsPlaying(false)

        }else if(!onSound && !isPlaying) {
            setIsPlaying(!isPlaying)
        }
    }
    
    
    
    //Play Actions
    
 

  
   
    const play = ()=>{
        setTimeLeft(gameTime)
        setGameStarted(!gameStarted);
        if(onSound){
            setIsPlaying(!isPlaying)
        }

        handleCountDown(gameTime);
    }
    

       // count down the game Timer
    
       let countDown;
   
    const handleCountDown = (time)=>{
         
           // let t = time;
           const timer = ()=>{
              
               time--;
            //    if(gameStarted){
            //        clearInterval(countDown)
            //    }
               if(time >= 0){
                   setTimeLeft(time);
               }else {
                   // setTimeLeft(t)
                   clearInterval(countDown);
                   // if(repeat){
                   //     setTimeLeft(t)
                   //     handleCountDown(t)
                   // }
               }
              
   
              
               
           }
           countDown = setInterval(timer, 1000);
           
           
        }


    // Handle End Button Click

    const onEndClick = ()=>{
        clearInterval(countDown);
        setGameStarted(!gameStarted)
        setTimeLeft(0);
       
        
    }
    
  
    
    //Update Game settings

    useEffect(()=>{
        //Backup user's settings
        const settings = { gameCat, gameLevel,onSound };
        const userSettings = JSON.stringify(settings)
        window.localStorage.setItem('settings', userSettings)


         // Set the timer based on user's level
    switch (gameLevel){

        case 'eazy':
            setGameTime(20)
            break;
            
        case 'medium':
            setGameTime(15);
            break;

        case 'hard':
            setGameTime(10);
            break;

        case 'wizard':
            setGameTime(5);
            break;
            
            default:
            setGameTime(20);


    }

    clearInterval(countDown)
    set
   

    }, [gameCat, gameLevel, onSound, gameTime, gameStarted])
    
   

    //Rendering Zone

    return (
        <>  
            <TopBar onclick={handleMenuClick} displayMenu={displayMenu} gameStarted={gameStarted}/>
            <MenuOptions  displayMenu={displayMenu}  toggleOnSound={toggleOnSound}  onSound={onSound} play={play} updateCategory={updateCategory} updateLevel={updateLevel} gameLevel={gameLevel}gameCat={gameCat} />
            <PlayButton play={play} displayMenu={displayMenu} gameStarted={gameStarted}/>
            <GamePlayInfo  gameStarted={gameStarted} timeLeft={timeLeft}/>
            <Sound playStatus={isPlaying && onSound ? Sound.status.PLAYING : Sound.status.STOPPED}  url={myD} loop={true}/>
           <EndButton gameStarted={gameStarted} handleClick={onEndClick}/>
           <QuestionInterface gameStarted={gameStarted} />
           <Footer />
        </>
    )
}

export default App
