const QuestionInterface = ({gameStarted, timeLeft, checkAnswer}) => {
    let anwers = ['Enugu', 'Abuja', 'Lagos', 'Aba']

    const style = {

    }

    return (
        

            <div className={`question-container ${gameStarted && (timeLeft >= 0) ? 'show-question' : ''}`}>
            <div className='question-display'>
            How old are you in the next three years juj jujj juj grhv juu hu jjth hjhthj hjvj htjhjh thjjthj jgujgugj jitgjbij ihiiji jhi ijhii ijhij ?
            </div>
            <div className='answers'>
                {anwers.map((an, id)=>(
                    setTimeout(()=>{
                        
                        <p key={id} onClick={checkAnswer} style={{background: `${timeLeft<=0 && an == 'Abuja' ? 'green': 'orange'}`, pointerEvents: `${timeLeft <= 0 ? 'none' : 'all'}`}}>{an}</p>
                    }, 800)
                   
                ))}
            </div>
            
        </div>
    )
}

export default QuestionInterface
