import TopBar from "./components/TopBar"
import MenuOptions from "./components/MenuOptions";
import PlayButton from "./components/PlayButton";
import Sound from 'react-sound';
import myD from './sounds/gameMu.mp3';

import { useState } from "react";

const App = () => {
   
    const [onSound, setOnSound] = useState(false);
    const [displayMenu, setDisplayMenu] = useState(false);
    const [isPlaying, setIsPlaying] = useState(false);

   
    // Menu Bar Actions

    const handleMenuClick = (element)=>{
        setDisplayMenu(!displayMenu);
    }


    //Play Actions

    const play = ()=>{

       setIsPlaying(!isPlaying)
      
    }


    //Handle Sound ON/OFF
    const toggleOnSound = (resp)=>{
        setOnSound(resp)
    }
    

    //
    return (
        <>  
            <TopBar onclick={handleMenuClick} displayMenu={displayMenu}/>
            <MenuOptions  displayMenu={displayMenu}  toggleOnSound={toggleOnSound}  onSound={onSound}/>
            <PlayButton play={play} displayMenu={displayMenu}/>
            <Sound playStatus={isPlaying ? Sound.status.PLAYING : Sound.status.STOPPED}  url={myD} loop={true}/>
        </>
    )
}

export default App
